import json
from datetime import datetime, timedelta

def handler(event, context):
    print('Event:', event)
    if 'queryStringParameters' not in event:
        return {'statusCode': 400, 'body': 'Invalid request'}
    path = event['queryStringParameters'].get('path', 'current_date')
    if path == 'current_date':
        return {'statusCode': 200, 'body': json.dumps({'date': datetime.now().strftime('%Y-%m-%d %H:%M:%S')})}
    elif path == 'date_math':
        if 'days' in event['queryStringParameters']:
            days = int(event['queryStringParameters']['days'])
            new_date = datetime.now() + timedelta(days=days)
            return {'statusCode': 200, 'body': json.dumps({'date': new_date.strftime('%Y-%m-%d %H:%M:%S')})}
        else:
            return {'statusCode': 400, 'body': 'Invalid date math request'}
    else:
        return {'statusCode': 400, 'body': 'Unknown path'}
