import dateutil.parser
import datetime
import json

def lambda_handler(event, context):
    request_type = event['requestType']
    if request_type == 'CurrentDate':
        return {'date': datetime.datetime.now().isoformat()}
    elif request_type == 'DateMath':
        base_date = dateutil.parser.parse(event['baseDate'])
        delta = datetime.timedelta(days=int(event['days']))
        calculated_date = base_date + delta
        return {'calculatedDate': calculated_date.isoformat()}
    else:
        raise ValueError('Invalid request type')
