import json
import datetime
def handler(event, context):
    resource_properties = event.get('ResourceProperties', {})
    operation = resource_properties.get('Operation', '')
    if operation == 'GetCurrentDate':
        return {'CurrentDate': datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')}
    elif operation == 'AddDays':
        input_date_str = resource_properties.get('Date', '')
        days_to_add = int(resource_properties.get('Days', 0))
        input_date = datetime.datetime.strptime(input_date_str, '%Y-%m-%d %H:%M:%S')
        new_date = input_date + datetime.timedelta(days=days_to_add)
        return {'NewDate': new_date.strftime('%Y-%m-%d %H:%M:%S')}
    else:
        return {'Error': 'Invalid operation provided.'}
